function find_minimum_cost(sentence, max_cost) {
    function dfs(start_index) {
        if (s[s.length - 1] - s[start_index] + n - start_index - 1 <= max_cost) {
            return 0;
        }
        var minimum_cost = Number.POSITIVE_INFINITY;
        var j = start_index + 1;
        while (j < n && (t = s[j] - s[start_index] + j - start_index - 1) <= max_cost) {
            minimum_cost = Math.min(minimum_cost, Math.pow(max_cost - t, 2) + dfs(j));
            j += 1;
        }
        return minimum_cost;
    }
    var word_lengths = sentence.split(" ").map(function (word) { return word.length; });
    var n = word_lengths.length;
    var s = [0].concat(word_lengths).map((sum => value => sum += value)(0));
    return dfs(0);
}
