// function to calculate the longest contiguous subarray with sum greater than zero
function f_gold(hours) {
    let ans = 0; // initialize the answer
    let s = 0; // initialize the sum
    let seen = {}; // initialize a dictionary to store the first occurrence of each sum
    for (let i = 0; i < hours.length; i++) { // loop through the array
        s += (hours[i] > 8) ? 1 : -1; // add 1 if hours[i] > 8, otherwise subtract 1
        if (s > 0) { // if the sum is greater than zero
            ans = i + 1; // update the answer
        }
        else { // if the sum is less than or equal to zero
            if (!(s in seen)) { // if the current sum has not been seen before
                seen[s] = i; // store the index of the current sum
            }
            if (s - 1 in seen) { // if there is a previous sum that is one less than the current sum
                ans = Math.max(ans, i - seen[s - 1]); // update the answer
            }
        }
    }
    return ans; // return the answer
}
