function find_adjacent_cells(rows, cols, center_row, center_col) {
    var queue = new Array();
    queue.push([center_row, center_col]);
    var visited = new Array(rows);
    for (var i = 0; i < rows; i++) {
        visited[i] = new Array(cols);
        for (var j = 0; j < cols; j++) {
            visited[i][j] = false;
        }
    }
    visited[center_row][center_col] = true;
    var result = new Array();
    while (queue.length > 0) {
        for (var i = 0; i < queue.length; i++) {
            var [row, col] = queue.shift();
            result.push([row, col]);
            for (var k = 0; k < 4; k++) {
                var row_offset = [1, -1, 0, 0];
                var col_offset = [0, 0, 1, -1];
                var x = row + row_offset[k];
                var y = col + col_offset[k];
                if (0 <= x && x < rows && 0 <= y && y < cols && !visited[x][y]) {
                    queue.push([x, y]);
                    visited[x][y] = true;
                }
            }
        }
    }
    return result;
}
