// define a function that finds missing numbers in an array of integers
function f_gold(nums) {
    // iterate through each number in the array
    for (let num of nums) {
        // calculate the index of the number
        let idx = Math.abs(num) - 1;
        // if the number at the calculated index is positive, make it negative
        if (nums[idx] > 0) {
            nums[idx] *= -1;
        }
    }
    // map the positive numbers to their corresponding indices and filter out null values
    return nums.map((n, i) => n > 0 ? i + 1 : null).filter(x => x !== null);
}
