// function to check if a matrix can be rotated to match the target matrix
function f_gold(mat, target) {
    // function to rotate a matrix by 90 degrees clockwise
    function rotate(matrix) {
        var n = matrix.length;
        for (var i = 0; i < Math.floor(n / 2); i++) {
            for (var j = i; j < n - 1 - i; j++) {
                var t = matrix[i][j];
                matrix[i][j] = matrix[n - j - 1][i];
                matrix[n - j - 1][i] = matrix[n - i - 1][n - j - 1];
                matrix[n - i - 1][n - j - 1] = matrix[j][n - i - 1];
                matrix[j][n - i - 1] = t;
            }
        }
    }
    // check if the matrix matches the target matrix after 0, 1, 2, or 3 rotations
    for (var _ = 0; _ < 4; _++) {
        if (JSON.stringify(mat) == JSON.stringify(target)) {
            return true;
        }
        rotate(mat);
    }
    return false;
}
