// define a function that takes an answer key and a number k as input
function f_gold(answerKey, k) {
    // define a nested function that takes a character c and a number k as input
    function get(c, k) {
        // initialize variables l and r to -1
        var l = -1, r = -1;
        // loop through the answer key
        while (r < answerKey.length - 1) {
            r += 1;
            // if the current character is equal to c, decrement k
            if (answerKey[r] == c) {
                k -= 1;
            }
            // if k is less than 0, increment l until the count of character c is balanced
            if (k < 0) {
                l += 1;
                if (answerKey[l] == c) {
                    k += 1;
                }
            }
        }
        // return the count of character c between l and r
        return r - l;
    }
    // return the maximum count of 'T' and 'F' characters with k skips
    return Math.max(get('T', k), get('F', k));
}
