function is_number_golden(num) {
    var count = {};
    for (var i = 0; i < num.length; i++) {
        if (count[num[i]] == undefined) {
            count[num[i]] = 1;
        } else {
            count[num[i]] += 1;
        }
    }
    for (var i = 0; i < num.length; i++) {
        if (parseInt(num[i]) != (count[i] || 0)) {
            return false;
        }
    }
    return true;
}
