// a function that modifies an array in place
function f_gold(arr) {
    var n = arr.length;
    var i = -1;
    var k = 0;
    // find the index of the middle element(s)
    while (k < n) {
        i += 1;
        k += (arr[i]) ? 1 : 2;
    }
    var j = n - 1;
    // if the array has an odd length, set the last element to 0
    if (k == n + 1) {
        arr[j] = 0;
        i = i - 1;
        j = j - 1;
    }
    // copy elements from the middle to the end of the array
    while (~j) {
        if (arr[i] == 0) {
            arr[j] = arr[j - 1] = arr[i];
            j -= 1;
        }
        else {
            arr[j] = arr[i];
        }
        i = i - 1;
        j = j - 1;
    }
}
