// define a function that calculates the gold value of a word
function f_gold(word) {
    var ans = 0, prev = 0;
    // loop through each character in the word
    for (var c of word) {
        // calculate the difference between the ASCII value of the current character and 'a'
        var curr = c.charCodeAt(0) - 'a'.charCodeAt(0);
        // calculate the minimum distance between the current and previous character in the alphabet
        var t = Math.abs(prev - curr);
        t = Math.min(t, 26 - t);
        // add the distance plus 1 to the answer
        ans += t + 1;
        // set the previous character to the current character
        prev = curr;
    }
    // return the final answer
    return ans;
}
```
