function count_digit_one(n) {
    var dp = new Array(10).fill(new Array(10).fill(-1));
    var digits = [];
    while (n) {
        digits.push(n % 10);
        n = Math.floor(n / 10);
    }
    function dfs(position, count, is_limit) {
        if (position == -1) {
            return count;
        }
        if (!is_limit && dp[position][count] != -1) {
            return dp[position][count];
        }
        var upper_limit = is_limit ? digits[position] : 9;
        var answer = 0;
        for (var i = 0; i < upper_limit + 1; i++) {
            var next_count = i == 1 ? count + 1 : count;
            answer += dfs(position - 1, next_count, is_limit && i == digits[position]);
        }
        if (!is_limit) {
            dp[position][count] = answer;
        }
        return answer;
    }
    return dfs(digits.length - 1, 0, true);
}
