function find_palindromic_substrings(s) {
    var palindromic_substrings = [];
    var n = s.length;
    var dp = new Array(n);
    for (var i = 0; i < n; i++) {
        dp[i] = new Array(n).fill(true);
    }
    for (var i = n - 1; i >= 0; i--) {
        for (var j = i + 1; j < n; j++) {
            dp[i][j] = s[i] == s[j] && dp[i + 1][j - 1];
        }
    }
    function dfs(s, i, t) {
        if (i == n) {
            palindromic_substrings.push(t.slice());
            return;
        }
        for (var j = i; j < n; j++) {
            if (dp[i][j]) {
                t.push(s.substring(i, j + 1));
                dfs(s, j + 1, t);
                t.pop();
            }
        }
    }
    dfs(s, 0, []);
    return palindromic_substrings;
}
