// function to find the length of the longest substring with at most one vowel of each type
const f_gold = (s) => {
    // initialize an array with 32 elements, all set to Infinity
    let pos = new Array(32).fill(Infinity);
    // set the first element of the array to -1
    pos[0] = -1;
    // define a string of vowels
    let vowels = 'aeiou';
    // initialize variables for state and ans
    let state = 0, ans = 0;
    // loop through each character in the input string
    for (let i = 0; i < s.length; i++) {
        // loop through each vowel in the vowels string
        for (let j = 0; j < vowels.length; j++) {
            // if the current character is a vowel, update the state variable
            if (s[i] == vowels[j]) {
                state ^= 1 << j;
            }
        }
        // update the ans variable with the maximum value between the current ans and the difference between i and the value at pos[state]
        ans = Math.max(ans, i - pos[state]);
        // update the value at pos[state] with the minimum value between the current value and i
        pos[state] = Math.min(pos[state], i);
    }
    // return the final value of ans
    return ans;
}
