                matrix[n - j - 1][i] = matrix[n - i - 1][n - j - 1];
                matrix[n - i - 1][n - j - 1] = matrix[j][n - i - 1];
                matrix[j][n - i - 1] = t;
            }
        }
    }
    for (var _ = 0; _ < 4; _++) {
        if (JSON.stringify(mat) == JSON.stringify(target)) {
            return true;
        }
        rotate(mat);
    }
    return false;
}


