function convert_numbers(mapping, nums) {
    var converted_nums = [];
    for (var i = 0; i < nums.length; i++) {
        var original_num = nums[i];
        var converted_num = 0;
        var multiplier = 1;
        while (1) {
            var digit = original_num % 10;
            original_num = Math.floor(original_num / 10);
            digit = mapping[digit];
            converted_num = digit * multiplier + converted_num;
            multiplier *= 10;
            if (original_num == 0) {
                break;
            }
        }
        converted_nums.push([converted_num, i, nums[i]]);
    }
    converted_nums.sort((a, b) => a[0] != b[0] ? a[0] - b[0] : (a[1] != b[1] ? a[1] - b[1] : a[2] - b[2]));
    for (var i = 0; i < converted_nums.length; i++) {
        nums[i] = converted_nums[i][2];
    }
    return nums;
}
