function find_max_score(scores, ages) {
    var players = Array.from(Array(scores.length), (_, k) => [ages[k], scores[k]]);
    players.sort((a, b) => a[0] !== b[0] ? a[0] - b[0] : a[1] - b[1]);
    var n = players.length;
    var max_scores = players.map(function (player) { return player[1]; });
    for (var i = 0; i < n; i++) {
        for (var j = 0; j < i; j++) {
            if (players[i][1] >= players[j][1]) {
                max_scores[i] = Math.max(max_scores[i], max_scores[j] + players[i][1]);
            }
        }
    }
    return Math.max.apply(Math, max_scores);
}
