```javascript
// function to apply discount to dollar amounts in a sentence
function f_gold(sentence, discount) {
    var ans = [];
    // split the sentence into words and loop through them
    for (var w of sentence.split(/\s+/)) {
        // check if the word starts with '$' and the rest of the word is a number
        if (w[0] == '$' && /^\d+$/.test(w.slice(1))) {
            // calculate the discounted amount and push it to the answer array
            var t = parseInt(w.substring(1)) * (1 - discount / 100);
            ans.push('$' + t.toFixed(2));
        }
        else {
            // push the word as-is to the answer array
            ans.push(w);
        }
    }
    // join the answer array into a sentence and return it
    return ans.join(' ');
}
```
