// function to find the maximum subarray sum
function f_gold(nums) {
    // initialize variables to store the maximum and minimum subarray sums
    var s1 = nums[0], s2 = nums[0], f1 = nums[0], f2 = nums[0];
    // loop through the array and update the variables accordingly
    for (var num of nums.slice(1)) {
        f1 = num + Math.max(f1, 0);
        f2 = num + Math.min(f2, 0);
        s1 = Math.max(s1, f1);
        s2 = Math.min(s2, f2);
    }
    // return the maximum subarray sum
    return s1 <= 0 ? s1 : Math.max(s1, nums.reduce((a, b) => a + b) - s2);
}
