// define a function that takes a string as input and returns the minimum number of times the word "balloon" can be formed using the letters of the input string
function f_gold(text) {
    // initialize an empty object to store the count of each letter in the input string
    let counter = {};
    // loop through each character in the input string
    for (let i = 0; i < text.length; i++) {
        // if the character is already in the counter object, increment its count
        if (counter[text[i]]) {
            counter[text[i]]++;
        } else { // otherwise, add the character to the counter object with a count of 1
            counter[text[i]] = 1;
        }
    }
    // divide the count of 'l' and 'o' by 2, since they appear twice in the word "balloon"
    counter['l'] >>= 1;
    counter['o'] >>= 1;
    // initialize a variable to store the minimum count of any letter in the word "balloon"
    let min = Number.MAX_SAFE_INTEGER;
    // loop through each letter in the word "balloon"
    for (let i = 0; i < 'balon'.length; i++) {
        // if the letter is in the counter object, update the minimum count if necessary
        if (('balon'[i] in counter ? counter['balon'[i]] : 0) < min) {
            min = 'balon'[i] in counter ? counter['balon'[i]] : 0;
        }
    }
    // return the minimum count of any letter in the word "balloon"
    return min;
}
