```javascript
function calculate_golden_number(N) {
    var operation = 0;
    var stack = [N];
    for (var i = N - 1; i > 0; i--) {
        if (operation == 0) {
            stack.push(stack.pop() * i);
        }
        else if (operation == 1) {
            stack.push(Math.trunc(stack.pop() / i));
        }
        else if (operation == 2) {
            stack.push(i);
        }
        else {
            stack.push(-i);
        }
        operation = (operation + 1) % 4;
    }
    return stack.reduce(function (accumulator, currentValue) {
        return accumulator + currentValue;
    });
}
