from typing import *
def f_gold(s: str) -> str:
    n = len(s)
    ans = ''
    for i in range(n):
        lower = upper = 0
        for j in range(i, n):
            if s[j].islower():
                lower |= 1 << (ord(s[j]) - ord('a'))
            else:
                upper |= 1 << (ord(s[j]) - ord('A'))
            if lower == upper and j - i + 1 > len(ans):
                ans = s[i : j + 1]
    return ans
