function count_minimum_occurrences_of_balloon(text) {
    let character_count = {};
    for (let i = 0; i < text.length; i++) {
        if (character_count[text[i]]) {
            character_count[text[i]]++;
        } else {
            character_count[text[i]] = 1;
        }
    }
    character_count['l'] >>= 1;
    character_count['o'] >>= 1;
    let min_occurrences = Number.MAX_SAFE_INTEGER;
    for (let i = 0; i < 'balon'.length; i++) {
        if (('balon'[i] in character_count ? character_count['balon'[i]] : 0) < min_occurrences) {
            min_occurrences = 'balon'[i] in character_count ? character_count['balon'[i]] : 0;
        }
    }
    return min_occurrences;
}
