function findLongestKey(releaseTimes, keysPressed) {
    var longestKey = keysPressed[0];
    var longestDuration = releaseTimes[0];
    for (var i = 1; i < keysPressed.length; i++) {
        var duration = releaseTimes[i] - releaseTimes[i - 1];
        if (duration > longestDuration || (duration == longestDuration && keysPressed.charCodeAt(i) > longestKey.charCodeAt(0))) {
            longestDuration = duration;
            longestKey = keysPressed[i];
        }
    }
    return longestKey;
}
