function find_vulnerable_points(m, n, guards, walls) {
    var grid = new Array(m);
    for (var i = 0; i < m; i++) {
        grid[i] = new Array(n);
    }
    for (var i = 0; i < guards.length; i++) {
        var row = guards[i][0];
        var col = guards[i][1];
        grid[row][col] = 'g';
    }
    for (var i = 0; i < walls.length; i++) {
        var row = walls[i][0];
        var col = walls[i][1];
        grid[row][col] = 'w';
    }
    for (var i = 0; i < guards.length; i++) {
        var row = guards[i][0];
        var col = guards[i][1];
        for (var k = 0; k < 4; k++) {
            var row_offset = [0, 0, 1, -1][k];
            var col_offset = [-1, 1, 0, 0][k];
            var x = row;
            var y = col;
            while (0 <= x + row_offset && x + row_offset < m && 0 <= y + col_offset && y + col_offset < n && grid[x + row_offset][y + col_offset] != 'w' && grid[x + row_offset][y + col_offset] != 'g') {
                x = x + row_offset;
                y = y + col_offset;
                grid[x][y] = 'v';
            }
        }
    }
    var sum = 0;
    for (var i = 0; i < grid.length; i++) {
        for (var j = 0; j < grid[0].length; j++) {
            var value = grid[i][j];
            sum = sum + (!value);
        }
    }
    return sum;
}
