// function to find the minimum number of perfect squares that sum up to n
function f_gold(n) {
    // create an array of size n+1 and initialize it with 0
    var dp = new Array(n + 1).fill(0);
    // loop through all numbers from 1 to n
    for (var i = 1; i <= n; i++) {
        var j = 1;
        var mi = Number.POSITIVE_INFINITY;
        // loop through all perfect squares less than or equal to i
        while (j * j <= i) {
            // find the minimum value of dp[i-j*j]
            mi = Math.min(mi, dp[i - j * j]);
            j += 1;
        }
        // set dp[i] to the minimum value found plus 1
        dp[i] = mi + 1;
    }
    // return the value of dp[n]
    return dp[n];
}
