function find_min_insertions_to_form_palindrome(str) {
    let str_length = str.length;
    let is_palindrome = new Array(str_length);
    for (let i = 0; i < str_length; i++) {
        is_palindrome[i] = new Array(str_length).fill(false);
    }
    for (let i = str_length - 1; i >= 0; i--) {
        for (let j = i; j < str_length; j++) {
            is_palindrome[i][j] = str[i] == str[j] && (j - 1 < 3 || is_palindrome[i + 1][j - 1]);
        }
    }
    let min_insertions = new Array(str_length).fill(0);
    for (let i = 0; i < str_length; i++) {
        if (!is_palindrome[0][i]) {
            min_insertions[i] = i;
            for (let j = 1; j <= i; j++) {
                if (is_palindrome[j][i]) {
                    min_insertions[i] = Math.min(min_insertions[i], min_insertions[j - 1] + 1);
                }
            }
        }
    }
    return min_insertions[str_length - 1];
}
