// define a function that returns the k largest elements from an array of numbers
function f_gold(nums, k) {
    // create an array of indices for the input array
    var idx = Array.from(Array(nums.length).keys());
    // sort the indices based on the corresponding values in the input array
    idx.sort(function (i, j) { return nums[i] - nums[j]; });
    // return the k largest elements from the input array
    return idx.slice(-k).sort((a, b) => (a - b)).map(i => nums[i]);
}
