function countWordsWithoutBrokenLetters(text, brokenLetters) {
    var brokenLetterSet = new Set(brokenLetters);
    var count = 0;
    for (var word of text.split(/\s+/)) {
        var containsBrokenLetter = false;
        for (var letter of brokenLetterSet) {
            if (word.includes(letter)) {
                containsBrokenLetter = true;
                break;
            }
        }
        if (!containsBrokenLetter) {
            count += 1;
        }
    }
    return count;
}
