    var ans = 0;
    var counter = {}; // create an empty object to store the count of each number
    for (var num of nums) {
        // add the count of numbers with difference k to the answer
        ans += (num - k in counter ? counter[num - k] : 0) + (num + k in counter ? counter[num + k] : 0);
