function filter_asteroids(asteroids) {
    let filtered_asteroids = [];
    for (let asteroid of asteroids) {
        if (asteroid > 0) {
            filtered_asteroids.push(asteroid);
        } else {
            while (filtered_asteroids.length > 0 && filtered_asteroids[filtered_asteroids.length - 1] > 0 && filtered_asteroids[filtered_asteroids.length - 1] < -asteroid) {
                filtered_asteroids.pop();
            }
            if (filtered_asteroids.length > 0 && filtered_asteroids[filtered_asteroids.length - 1] == -asteroid) {
                filtered_asteroids.pop();
            } else if (filtered_asteroids.length == 0 || filtered_asteroids[filtered_asteroids.length - 1] < -asteroid) {
                filtered_asteroids.push(asteroid);
            }
        }
    }
    return filtered_asteroids;
}
