def f_gold(s: str) -> str:
    base = 131
    mod = 10**9 + 7
    n = len(s)
    prefix = suffix = 0
    mul = 1
    idx = 0
    for i, c in enumerate(s):
        prefix = (prefix * base + (ord(c) - ord('a') + 1)) % mod
        suffix = (suffix + (ord(c) - ord('a') + 1) * mul) % mod
        mul = (mul * base) % mod
        if prefix == suffix:
