function find_longest_palindrome(s) {
    var base = 131;
    var mod = 1000000000 + 7;
    var n = s.length;
    var prefix_hash = 0, suffix_hash = 0;
    var base_power = 1;
    var palindrome_end_index = 0;
    for (var i = 0; i < s.length; i++) {
        prefix_hash = (prefix_hash * base + (s.charCodeAt(i) - 'a'.charCodeAt(0) + 1)) % mod;
        suffix_hash = (suffix_hash + (s.charCodeAt(i) - 'a'.charCodeAt(0) + 1) * base_power) % mod;
        base_power = (base_power * base) % mod;
        if (prefix_hash == suffix_hash) {
            palindrome_end_index = i + 1;
        }
    }
    return (palindrome_end_index == n) ? s : Array.from(s.slice(palindrome_end_index)).reverse().join("") + s;
}
