// define a function to count the number of digit 1 in the range [1, n]
function f_gold(n) {
    // initialize a 2D array with -1
    var dp = new Array(10).fill(new Array(10).fill(-1));
    // convert the number to an array of digits
    var digit = [];
    while (n) {
        digit.push(n % 10);
        n = Math.floor(n / 10);
    }
    // define a recursive function to count the number of digit 1
    function dfs(pos, cnt, limit) {
        if (pos == -1) {
            return cnt;
        }
        if (!limit && dp[pos][cnt] != -1) {
            return dp[pos][cnt];
        }
        var up = limit ? digit[pos] : 9;
        var ans = 0;
        for (var i = 0; i < up + 1; i++) {
            var nxt = i == 1 ? cnt + 1 : cnt;
            ans += dfs(pos - 1, nxt, limit && i == digit[pos]);
        }
        if (!limit) {
            dp[pos][cnt] = ans;
        }
        return ans;
    }
    // return the result of the recursive function
    return dfs(digit.length - 1, 0, true);
}
