"use strict";

const f_gold = (n, roads) => {
    let deg = new Array(n).fill(0);
    for (let i = 0; i < roads.length; i++) {
        let [a, b] = roads[i];
        deg[a] += 1;
        deg[b] += 1;
    }
    deg.sort((a, b) => a - b);
    return deg.reduce((acc, v, i) => acc + (i + 1) * v, 0);
};
