// function to convert a fraction to a string in decimal form
function f_gold(numerator, denominator) {
    if (numerator == 0) {
        return '0';
    }
    var res = [];
    // check if the result is negative
    var neg = (numerator > 0) ^ (denominator > 0);
    if (neg) {
        res.push('-');
    }
    var num = Math.abs(numerator);
    var d = Math.abs(denominator);
    // add the integer part of the result
    res.push(String(Math.floor(num / d)));
    num %= d;
    // if the result is an integer, return it
    if (num == 0) {
        return res.join('');
    }
    // add the decimal point
    res.push('.');
    var mp = {};
    // add the fractional part of the result
    while (num != 0) {
        mp[num] = res.length;
        num *= 10;
        res.push(String(Math.floor(num / d)));
        num %= d;
        // if the result is repeating, add parentheses
        if (num in mp) {
            var idx = mp[num];
            res.splice(idx, 0, '(');
            res.push(')');
            break;
        }
    }
    return res.join('');
}
