```javascript
function find_possible_combinations(digits) {
    var num_digits = digits.length;
    if (num_digits == 0) {
        return [];
    }
    var digit_to_char_map = ['abc', 'def', 'ghi', 'jkl', 'mno', 'pqrs', 'tuv', 'wxyz'];
    var digit_to_char_strings = [];
    for (var digit of digits) {
        digit_to_char_strings.push(digit_to_char_map[parseInt(digit) - 2]);
    }
    var result = [];
    for (var char_string of digit_to_char_strings) {
        if (result.length === 0) {
            result = Array.from(char_string);
        }
        else {
            var cache = [];
            for (var item of result) {
                for (var letter of char_string) {
                    cache.push(item + letter);
                }
            }
            result = cache;
        }
    }
    return result;
}
```
