```javascript
// function to find the smallest amount of coins needed to make change for a given amount
function f_gold(coins) {
    var res = 1; // initialize the result to 1
    for (var coin of coins.sort((a, b) => (a - b))) { // sort the coins in ascending order
        if (coin > res) { // if the current coin is greater than the result, break the loop
            break;
        }
        res += coin; // add the current coin to the result
    }
    return res; // return the result
}
```
