// define a function that takes an array of integers as input and returns the number of subsets whose bitwise OR equals the maximum value in the array
function f_gold(nums) {
    // initialize variables
    var mx = 0, ans = 0;
    // calculate the bitwise OR of all elements in the array
    for (var x of nums) {
        mx |= x;
    }
    // define a recursive function to calculate the number of subsets whose bitwise OR equals the maximum value
    function dfs(i, t) {
        // base case: if we have processed all elements in the array
        if (i == nums.length) {
            // if the bitwise OR of the current subset equals the maximum value, increment the answer
            if (t == mx) {
                ans++;
            }
            return;
        }
        // recursive case: include or exclude the current element in the subset
        dfs(i + 1, t);
        dfs(i + 1, t | nums[i]);
    }
    // call the recursive function with initial parameters
    dfs(0, 0);
    // return the answer
    return ans;
}
